import React,{useState,useEffect} from 'react';
import io from 'socket.io-client';
import queryString from 'query-string';
import {useLocation} from 'react-router-dom';

let socket;
export default function Chat()
{
    let location = useLocation();
    const [name,setName]= useState('');
    const [channel,setChannel]= useState('');
    const [message,setMessage] = useState('');
    const sendMessage  = () =>
    useEffect(()=>{
        const {name,channel} = queryString.parse(location.search);  
        socket = io.connect('http://localhost:3001');//connect to server 
        console.log(name,channel);
        setName(name);
        setChannel(channel);
        socket.emit('join',{name,channel});
        return () => {
            socket.emit('disconnect');
            socket.off();
        }
    },[location.search]);//render only when value in array changes
    
    return(
        <div>
            <div className = "chat-header">
                <a>Get Started</a>
            </div>
            <div className = "chat-body">

            </div>
            <div className = "chat-footer">
                <input type = "text" onChange  = {(event)=>{setMessage(event.target.value);}}/>
                <button type = "submit"> Send</button>
            </div>
        </div>
    )
}
