import React,{useState,useEffect} from 'react';
import io from 'socket.io-client';
import queryString from 'query-string';
import {useLocation} from 'react-router-dom';
export default function Chat()
{
    const {search} = useLocation();
    const [name,setName]= useState('');
    const [channel,setChannel]= useState('');
    const [message,setMessage] = useState('');
    const [messageList,setMessageList] = useState([]);
    const [socket,setSocket] = useState
    const sendMessage  = async () => {
        if(message!=''){
          await socket.emit('send_message',{channel, name,message});
          setMessageList((messageList)=>[...messageList,{channel, name,message}]);
        }
    };
    useEffect(()=>{
        socket = io('http://localhost:3001');//connect to server 
        const {name,channel} = queryString.parse(search);  
        setName(name);
        setChannel(channel);
        socket.emit('join',{name,channel});
        return () => {
            socket.emit('disconnect');
            socket.off();
        }
    },[search]);//render only when value in array changes

    useEffect(()=>{
        console.log(socket);
        socket.on('receive_message',(data)=>{
            setMessageList((messageList)=>[...messageList,data]);
        });
    },[socket]);
    return(
        <div> 
            <div className = "chat-header">
                <a>Get Started</a>
            </div>
            <div className = "chat-body">
                {messageList.map((message)=>{
                    return <h1>{message.message} - {message.name}</h1>
                })}
            </div>
            <div className = "chat-footer">
                <input type = "text" onChange  = {(event)=>{setMessage(event.target.value);}}/>
                <button type = "submit" onClick = {sendMessage}> Send</button>
            </div>
        </div>
    )
}
